  url, create_time, modify_time
  )
values (#{id,jdbcType=INTEGER}, #{productId,jdbcType=INTEGER}, #{shortUrl,jdbcType=VARCHAR}, 
  #{url,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{modifyTime,jdbcType=TIMESTAMP}
  )

mapper.select:
